import React, { useState } from "react";

const MovieSearch = ({ getRecommendations, setError, setRecommendations }) => {
  const [movieName, setMovieName] = useState("");
  const [year, setYear] = useState("");
  const [actor, setActor] = useState("");

  const handleSearch = async () => {
    try {
      setError(null);
      if (!movieName) {
        setError("Movie name is required.");
        return;
      }

      const recommendations = await getRecommendations(movieName, year, actor);
      setRecommendations(recommendations);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="search-container">
      <input
        type="text"
        placeholder="Enter movie name"
        value={movieName}
        onChange={(e) => setMovieName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter year (optional)"
        value={year}
        onChange={(e) => setYear(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter actor (optional)"
        value={actor}
        onChange={(e) => setActor(e.target.value)}
      />
      <button onClick={handleSearch}>Recommend</button>
    </div>
  );
};

export default MovieSearch;
