import React, { useState, useEffect } from "react";
import MovieSearch from "./components/MovieSearch";
import RecommendationList from "./components/RecommendationList";
import "./App.css";
import logo from './logo.svg';

const App = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true); // state to track loading

  const getRecommendations = async (movieName, year, actor) => {
    try {
      const response = await fetch("http://localhost:5000/recommend", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ movie_name: movieName, year, actor }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Error fetching recommendations.");
      }

      return await response.json();
    } catch (error) {
      throw error;
    }
  };

  // Simulate loading by setting isLoading to false after 3 seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false); // hide loading after 3 seconds
    }, 3000);

    return () => clearTimeout(timer); // cleanup the timer if the component is unmounted
  }, []);

  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-screen">
          <img src={logo} className="App-logo" alt="logo" />
          <h2>Loading...</h2>
        </div>
      ) : (
        <>
          <h1>Movie Recommendation System</h1>
          <MovieSearch
            getRecommendations={getRecommendations}
            setError={setError}
            setRecommendations={setRecommendations}
          />
          {error && <div className="error">{error}</div>}
          <RecommendationList recommendations={recommendations} />
        </>
      )}
    </div>
  );
};

export default App;
