import React, { useState } from "react";
import MovieSearch from "./components/MovieSearch";
import RecommendationList from "./components/RecommendationList";
import "./App.css";

const App = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState(null);
  const [activePage, setActivePage] = useState("app"); // Manage active section
  const [showFooterModal, setShowFooterModal] = useState(null); // Manage modal visibility

  const handlePageChange = (page) => {
    setActivePage(page);
  };

  const handleFooterModal = (section) => {
    setShowFooterModal(section);
  };

  const closeFooterModal = () => {
    setShowFooterModal(null);
  };

  return (
    <div className="app">
      <nav className="navbar">
        <h1>Popcorn Picks</h1>
        <div className="nav-links">
          <button onClick={() => handlePageChange("app")}>App</button>
          <button onClick={() => handlePageChange("about")}>About Me</button>
        </div>
      </nav>

      {/* Main Page Content */}
      {activePage === "app" && (
        <div className="app-content">
          <MovieSearch setError={setError} setRecommendations={setRecommendations} />
          {error && <div className="error">{error}</div>}
          <RecommendationList recommendations={recommendations} />
        </div>
      )}

      {activePage === "about" && (
        <div className="about-content">
          <h2>About Me</h2>
          <p>
            Hello! I'm a passionate developer building this movie recommendation app.
            Feel free to explore the app and get personalized movie recommendations.
          </p>
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <button onClick={() => handleFooterModal("about")}>About</button>
        <button onClick={() => handleFooterModal("contact")}>Contact</button>
      </footer>

      {/* Footer Modal Content */}
      {showFooterModal && (
        <div className="modal">
          <div className="modal-content">
            <button className="close-button" onClick={closeFooterModal}>
              &times;
            </button>
            {showFooterModal === "about" && (
              <>
                <h2>About</h2>
                <p>
                  Popcorn Picks is a movie recommendation system designed to help you find your
                  next favorite movie based on your preferences. Built with passion and care!
                </p>
              </>
            )}
            {showFooterModal === "contact" && (
              <>
                <h2>Contact</h2>
                <p>
                  Have questions or feedback? Reach out to us at:
                  <br />
                  <strong>Email:</strong> support@popcornpicks.com
                  <br />
                  <strong>Phone:</strong> +1-800-MOVIE-42
                </p>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
